CREATE TABLE IF NOT EXISTS `test`.`employee` (  `id` INT(11) NOT NULL AUTO_INCREMENT,  `fname` VARCHAR(32) NOT NULL,  `lname` VARCHAR(32) NOT NULL,  `address1` VARCHAR(32) NULL DEFAULT NULL,  `city` VARCHAR(20) NULL DEFAULT NULL,  `state` VARCHAR(2) NULL DEFAULT NULL,  `zipcode` INT(9) NULL DEFAULT NULL,  PRIMARY KEY (`id`),  UNIQUE INDEX `fname_lname_UNIQUE` (`fname` ASC, `lname` ASC));

CREATE TABLE IF NOT EXISTS `test`.`transaction_rec` (  `id` INT(11) NOT NULL AUTO_INCREMENT,  `date` DATE NOT NULL,  `category` VARCHAR(32) NOT NULL,  `description` VARCHAR(100) NULL DEFAULT NULL,  `amount` DECIMAL(18,3) NULL DEFAULT NULL,  `taxName` VARCHAR(32) NULL DEFAULT NULL,  `taxAmount` DECIMAL(18,3) NULL DEFAULT NULL,  `type` INT(1) NULL DEFAULT NULL,  `emp_id` INT(11) NOT NULL,  PRIMARY KEY (`id`),  INDEX `employee__id_FK_idx` (`emp_id` ASC),  CONSTRAINT `employee__id_FK`    FOREIGN KEY (`emp_id`)    REFERENCES `test`.`employee` (`id`)    ON DELETE NO ACTION    ON UPDATE NO ACTION);